

SELECT id, create_date_time,0.0 FROM social_message
WHERE id NOT IN (SELECT message_id FROM message_entity_tsvector);

SELECT s.id, s.create_date_time,'vader', 'neutral', 0.0
FROM social_message s
WHERE NOT EXISTS (
    SELECT 1 FROM message_entity_sentiment m
    WHERE m.message_id = s.id AND m.model_name = 'vader'
);

SELECT id, create_date_time,CAST('{}' AS JSON),CAST(ARRAY[] AS REAL ARRAY) FROM social_message
WHERE id NOT IN (SELECT message_id FROM message_entity_tsvector);

SELECT id, create_date_time,'4326', 'POINT(-78.5 38.0)', 'United States', '0101000020E61000000000000000A053C00000000000004340' FROM social_message
WHERE id NOT IN (SELECT message_id FROM message_entity_tsvector);